import { AiImpactModifier, ScenarioId, HorizonId, Confidence } from '@/lib/types';
import { cpiComponents } from './cpiComponents';

// Explanation map: per-scenario-per-horizon narratives
// Keys: b=baseline, m=moderate, t=transformative; 1=1yr, 3=3yr, 10=10yr
type ExplanationMap = {
  b1: string; b3: string; b10: string;
  m1: string; m3: string; m10: string;
  t1: string; t3: string; t10: string;
};

// Helper to create 9 modifier entries (3 scenarios x 3 horizons) for a component
// Call: m(id, [b1yr],[b3yr],[b10yr], [m1yr],[m3yr],[m10yr], [t1yr],[t3yr],[t10yr], explanations, citations)
// eslint-disable-next-line @typescript-eslint/no-explicit-any
function m(id: string, ...args: any[]): AiImpactModifier[] {
  // First 9 args are [impact, weight] pairs, then explanations (string or map), then citations array
  const pairs = args.slice(0, 9) as [number, number][];
  const explanations = args[9] as string | ExplanationMap;
  const citations = args[10] as string[];
  const confidence: [Confidence, Confidence, Confidence] = (args[11] as [Confidence, Confidence, Confidence]) || ['high', 'medium', 'low'];

  const scenarios: ScenarioId[] = ['conservative', 'moderate', 'transformative'];
  const horizons: HorizonId[] = ['1yr', '3yr', '10yr'];
  const sKeys = ['b', 'm', 't'];
  const hKeys = ['1', '3', '10'];
  const result: AiImpactModifier[] = [];
  for (let s = 0; s < 3; s++) {
    for (let h = 0; h < 3; h++) {
      const [impact, weight] = pairs[s * 3 + h];
      const expKey = `${sKeys[s]}${hKeys[h]}` as keyof ExplanationMap;
      result.push({
        componentId: id,
        scenario: scenarios[s],
        horizon: horizons[h],
        inflationImpactPp: impact,
        weightShiftPp: weight,
        confidence: confidence[h],
        explanation: typeof explanations === 'string' ? explanations : (explanations[expKey] || ''),
        citations,
      });
    }
  }
  return result;
}

