// Build O(1) lookup index
const modifierIndex: Record<string, AiImpactModifier> = {};
for (const mod of aiImpactModifiers) {
  modifierIndex[`${mod.componentId}_${mod.scenario}_${mod.horizon}`] = mod;
}

/**
 * Get modifier for a component, walking up the tree if no direct match found
 */
export function getModifier(
  componentId: string,
  scenario: ScenarioId,
  horizon: HorizonId
): AiImpactModifier | null {
  const key = `${componentId}_${scenario}_${horizon}`;
  const direct = modifierIndex[key];
  if (direct) return direct;

  // Walk up parent chain
  const component = cpiComponents[componentId];
  if (component?.parentId) {
    return getModifier(component.parentId, scenario, horizon);
  }
  return null;
}
